import React, { useState , useEffect }from 'react';
// import logo from './logo.svg';
import Book from '../src/objects/Book'
import logic from './logic'
import './App.css';

function App() {
  const [books , setBooks] = useState(undefined)

  useEffect(()=>{
    async function retrieveBooks(){
      try {
        const { items } = await logic.retrieveBooksFromAPI()
        setBooks(items)
        console.log('items' , items)
      } catch (error) {
        console.log(error)
      }
    }
    retrieveBooks()
  } , [setBooks])


  return (
    <div className="App">
      <header className="App-header">
        <h1>Google Books API</h1>
      </header>
      <section>
        <ul className = "list">
          {books && books.map( ({volumeInfo} , index) => {
            let currentBook = new Book(volumeInfo.title, volumeInfo.subtitle, volumeInfo.authors, volumeInfo.publish_date, volumeInfo.publisher, volumeInfo.categories)
            console.log('categories' , volumeInfo.categories)
            return (
                <li key={index.toString()} className="list__item">{`${currentBook.title} (${currentBook.subtitle }), ${currentBook.authors}, ${currentBook.publish_date}, ${currentBook.publisher}, [${currentBook.categories}]`}</li>
                )
        })}
      </ul>
      </section>
    </div>
  );
}

export default App;
