import React, { useState , useEffect }from 'react';
// import logo from './logo.svg';
import Book from '../src/objects/Book'
import logic from './logic'
import './App.css';

function App() {
  const [books , setBooks] = useState(undefined)

  useEffect(()=>{
    async function retrieveBooks(){
      try {
        const { items } = await logic.retrieveBooksFromAPI()
        setBooks(items)
        console.log('items' , items)
      } catch (error) {
        console.log(error)
      }
    }
    retrieveBooks()
  } , [setBooks])


  return (
    <div className="App">
      <header className="App-header">
        <h1>Google Books API</h1>
      </header>
      <section>
        <ul>
          {books && books.map( ({volumeInfo}) => {
            let currentBook = new Book(volumeInfo.title, volumeInfo.subtitle, volumeInfo.authors, volumeInfo.publish_date, volumeInfo.publisher, volumeInfo.categories)
            console.log('title' , currentBook.title)
            return <li>{volumeInfo.title}</li>
        })}
      </ul>
      </section>
    </div>
  );
}

export default App;
