import React, { useState , useEffect }from 'react';
// import logo from './logo.svg';
import Book from '../src/objects/Book'
import logic from './logic'
import './App.css';

function App() {
  const [books , setBooks] = useState(undefined)

  useEffect(()=>{
    async function retrieveBooks(){
      try {
        const { items } = await logic.retrieveBooksFromAPI()

        setBooks(items.sort(function(a, b) {
          return a.publishedDate - b.publishedDate;
        }))

      } catch (error) {
        console.log(error)
      }
    }
    retrieveBooks()
  } , [setBooks])


  return (
    <div className="App">
      <header className="App-header">
        <h1>Google Books API</h1>
      </header>
      <section>
        <ol className = "list">
          {books && books.map( ({volumeInfo} , index) => {
            let currentBook = new Book(volumeInfo.title, volumeInfo.subtitle, volumeInfo.authors, volumeInfo.publishedDate, volumeInfo.publisher, volumeInfo.categories)
            return (
                <li key={index} className="list__item">{`${currentBook.title}`}{`(${currentBook.subtitle}),`}{`${currentBook.authors}, ${currentBook.publish_date}, ${currentBook.publisher}, [${currentBook.categories}]`}</li>
                )
        })}
      </ol>
      </section>
    </div>
  );
}

export default App;
