import React, {useState , useEffect} from 'react';
// import logo from './logo.svg';
import Book from '../src/objects/Book'
import logic from './logic'
import utils from './utils'
import './App.css';

function App() {
  const [books , setBooks] = useState(undefined)

  useEffect(()=>{
    async function retrieveBooks(){
      try {
        let { items } = await logic.retrieveBooksFromAPI()

        items.map( item => {
          item.volumeInfo.publishedDate = utils.formatDate(item.volumeInfo.publishedDate)
          return item
        })

        setBooks(items)
      } catch (error) {
        console.log(error)
      }
    }
    retrieveBooks()
  } , [setBooks])

  useEffect( () => {
    if(books){
      const formatedDates = books.map(book=>{
        book.volumeInfo.publishedDate = utils.formatDate(book.volumeInfo.publishedDate)
        return book
      })
      setBooks(formatedDates)
    }
  }, [setBooks])


  return (
    <div className="App">
      <header className="App-header">
        <h1>Google Books API</h1>
      </header>
      <section>
        <ol className = "list">
          {books && books.map( ({volumeInfo} , index) => {
            let currentBook = new Book(volumeInfo.title, volumeInfo.subtitle, volumeInfo.authors, volumeInfo.publishedDate, volumeInfo.publisher, volumeInfo.categories)
            return (
                <li key={index} className="list__item">
                  <div>
                    {<p>{currentBook.title }</p>}
                    {currentBook.subtitle && <p>{currentBook.subtitle}</p>}
                  </div>
                  {<p>{currentBook.authors}</p>}
                  {<p>{currentBook.publisher}</p>}
                  {<p>{currentBook.publish_date}</p>}
                  {<p>{currentBook.categories}</p>}
                </li>
                )
        })}
      </ol>
      </section>
    </div>
  );
}

export default App;
